<section class="processes-section">
    <?php $pageid = url_to_postid(get_site_url().'/software-development-processes') ?>
    <?php if (get_the_ID() == $pageid){ ?>
        <h1>Processes</h1>
        <p class="section-description processes-description"><?php _e('As a customer oriented company we aim to incorporate greater transparency in all our processes from contract signing to the release day.', 'moc'); ?></p>
    <?php } else { ?>
      <h1>How we work</h1>
    <?php } ?>
    <ul class="processes-list js-mouseover">
      <?php
      $flex_content = get_field('processes', $pageid);
      if(!empty($flex_content)){ ?>

      <?php foreach($flex_content as $flex_block) { ?>
      <li class="processes-list__item <?php echo $flex_block['svg_id']; ?>">
        <span class="process-icon__holder icon-<?php echo $flex_block['svg_id']; ?>">
          <?php if ($flex_block['use_class']) {?>
            <svg class="process-icon">
                <use xlink:href="#icon-<?php echo $flex_block['svg_id']; ?>"></use>
            </svg>
          <?php } else { ?>
            <span class="process-icon">
                <img class="process-image" src="<?php echo $flex_block['image']; ?>" alt="<?php echo $flex_block['title']; ?>" />
            </span>
        <?php } ?>
        </span>
        <div class="processes-header__wrapper">
          <h2 class="processes-item__header"><?php echo $flex_block['title']; ?></h2>
        </div>
        <div class=" processes-item__content">
          <p class="text"><?php echo $flex_block['text']; ?></p>
        </div>
      </li>
      <?php } } ?>
    </ul>
  </section>
